import { createSearchParams, useNavigate } from "react-router-dom";

const SearchBar = ({ searchQuery, setSearchQuery, setData}) => {
    let navigate = useNavigate();
    const onSubmit = (e) => {
        if (searchQuery) {
            const params = { place_name: searchQuery};
            navigate({
                pathname: '/search',
                search: `?${createSearchParams(params)}`,
              });
            fetch("http://localhost:8000/places?=" + searchQuery)
                .then(response => response.json())
                .then(setData);
        }
        e.preventDefault();
    };

    return (
        <form onSubmit={onSubmit}>
            <input
                value={searchQuery}
                onInput={(e) => setSearchQuery(e.target.value)}
                type="text"
                id="header-search"
                placeholder="       Name a place, we hope to answer your questions!"
                name="s"
            />
            {/* <button type="submit">Search</button> */}
        </form>
    );
};

export default SearchBar;
