import React, { useState, useEffect } from 'react';
import DatePicker from "./DatePicker";
import { useSearchParams } from 'react-router-dom';
import Card from "react-bootstrap/Card";
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import Nav from 'react-bootstrap/Nav';
import Tab from 'react-bootstrap/Tab';
import './Result.css';
import Search from './search';
import logo from './travel-made-easy.svg';
import Container from 'react-bootstrap/Container';

const Result = (prop) => {

    var from = new Date();
    var dd = from.getDate();
    var mm = from.getMonth() + 1;
    var yyyy = from.getFullYear();
    if (dd < 10) {
        dd = '0' + dd;
    }
    if (mm < 10) {
        mm = '0' + mm;
    }
    from = yyyy + '-' + mm + '-' + dd;


    var to = new Date();
    to.setDate(to.getDate()+30);
    dd = to.getDate();
    mm = to.getMonth() + 1;
    yyyy = to.getFullYear();
    if (dd < 10) {
        dd = '0' + dd;
    }
    if (mm < 10) {
        mm = '0' + mm;
    }
    to = yyyy + '-' + mm + '-' + dd;

    const [searchParams] = useSearchParams();
    const [data, setData] = useState(null);
    const [fromDate, setFromDate] = useState(from);
    const [toDate, setToDate] = useState(to);
    searchParams.set('from', fromDate);
    searchParams.set('to', toDate);
    const [searchQuery, setSearchQuery] = useState(searchParams.get('place_name') || '');
    useEffect(() => {
        fetch("http://localhost:8000/places?=" + searchParams.get('place_name') + "?" + searchParams.get('from'))
            .then(response => response.json())
            .then(setData);
    }, [searchParams]);

    if (data != null && data[0]) {
        return (

            <Container fluid>
                <div className="result">
                    <Row>
                        <Col sm={1}>
                            <div className="result-logo">
                                <a href="/">
                                    <img src={logo} className="App-logo" alt="logo" />
                                </a>
                            </div>
                        </Col>
                        <Col sm={5} className="searchbar-container">
                            <Search
                                searchQuery={searchQuery}
                                setSearchQuery={setSearchQuery}
                            />
                        </Col>
                        <Col sm={3}>
                            <DatePicker
                                label="From"
                                date={fromDate}
                                setDate={setFromDate}
                            />
                        </Col>
                        <Col sm={3}>
                            <DatePicker
                                label="To"
                                date={toDate}
                                setDate={setToDate}
                            />
                        </Col>
                    </Row>
                    <div className="result-header">
                        <Row>
                            <Col sm={12}>
                                <Card>
                                    <Card.Body>
                                        <Card.Title className="title">{data[0].place_name}</Card.Title>
                                        {/* <Card.Text>{data[0].summary}</Card.Text> */}
                                    </Card.Body>
                                </Card>
                            </Col>
                        </Row>
                    </div>
                    <div className="result-tab">
                        <Row>
                            <Col sm={12}>
                                <Tab.Container justify fill id="left-tabs-example" defaultActiveKey="first">
                                    <Row>
                                        <Col sm={3}>
                                            <Nav variant="pills" className="flex-column">
                                                <Nav.Item>
                                                    <Nav.Link eventKey="first">Any food places nearby?</Nav.Link>
                                                </Nav.Item>
                                                <Nav.Item>
                                                    <Nav.Link eventKey="second">Is it safe?</Nav.Link>
                                                </Nav.Item>
                                            </Nav>
                                        </Col>
                                        <Col sm={9}>
                                            <Tab.Content>
                                                <Tab.Pane eventKey="first">
                                                    {data[0].food_places.map((review) => (
                                                        <Card>
                                                            <Card.Body>
                                                                <Card.Title>{review.reviewer}</Card.Title>
                                                                <Card.Text>{review.review_comment}</Card.Text>
                                                            </Card.Body>
                                                        </Card>
                                                    ))}
                                                </Tab.Pane>
                                                <Tab.Pane eventKey="second">
                                                    {data[0].is_it_safe.map((review) => (
                                                        <Card>
                                                            <Card.Body>
                                                                <Card.Title>{review.reviewer}</Card.Title>
                                                                <Card.Text>{review.review_comment}</Card.Text>
                                                            </Card.Body>
                                                        </Card>
                                                    ))}
                                                </Tab.Pane>
                                            </Tab.Content>
                                        </Col>
                                    </Row>
                                </Tab.Container>
                            </Col>
                        </Row>
                    </div>
                </div>
            </Container>
        );
    } else {
        return (
            <div />
        );
    }
};

export default Result;
